import React, { useState } from 'react';
import Lists from './ToDoLists';

const TodoList = () =>{

    let [listName, UpdateListName ] = useState("");
    let [items, setItems] = useState([]);
 
    const InputEvent = (event) =>{
            UpdateListName(event.target.value)
    }

    const listOfItems = () =>{
          setItems((oldItems) =>{
              return [...oldItems,listName]
              
          });


         
          UpdateListName("");
    }

    const deleteItems = (id) =>{
        console.log("deleted");
        setItems((oldItems) =>{
             return oldItems.filter((arrEle, index) =>{
                  return index !== id;
             })
        })
    }

    return (

      
        <>
        <div className='main_div'>
            <div className='center_div'>
                <br />
                <h1>ToDo List</h1>
                <br />
                <input  type="text" placeholder="Add a Items" onChange={InputEvent} value={listName} />
                <button onClick={listOfItems}>+</button>

                <ol>
                    {/* <li>{listName}</li> */}
                    {items.map((curVal, index) =>{
                         return <Lists 
                         key={index} 
                            id={index}
                             text= {curVal}
                             onSelect={deleteItems}
                         />
                    })}
                </ol>
            </div>
        </div>
        </>
    )
}


export default TodoList;
