import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Pokemon = () =>{
    let [num, setnum] = useState(1);
    let [name, setname] = useState();
    let [move, setmove] = useState();

    useEffect(() =>{
        // alert ("Hii");
        async function getData(){
            const response = await axios.get(`https://pokeapi.co/api/v2/pokemon/${num}`);
            console.log(response.data);
            setname(response.data.name);
            setmove(response.data.length(moves));
        } 
        getData();
    })
    return (
        <>
        <h1>You choose <span style={{color:'red'}}>{num} </span>value</h1>
        <h1>My name is <span style={{color:'red'}}>{name}</span></h1>
        <h1>I have <span style={{color:'red'}}>{move}</span> moves</h1>
        <select value={num} className="px-3 py-1 mx-2" onChange={(event) =>{
            setnum(event.target.value);

        }}>
            <option value="1">1</option>
            <option value="2">2</option>
            <option value="3">3</option>
            <option value="4">4</option>
            <option value="5">5</option>

        </select>
        </>
    )
}

export default Pokemon;
