import React, { useState } from 'react';
import Header from './Header';
import Footer from './Footer';
import CreateNote from './CreateNote';
import Note from './Note';
const Clone = () =>{
  const [addItem, setItem] = useState([]);

  const addNote = (note) =>{
    // alert ('i am clicked');
    setItem((oldData) =>{
         return [...oldData, note]
    })

    console.log(note);
    
  }

  const onDelete = (id) =>{
       setItem((oldData) =>{
           addItem.filter((val, index) =>{
               return index !== id;
           
           })
       })
  }
    return (
        <>
          <Header />
          <CreateNote 
          passNote={addNote} />
           
            {addItem.map((val, index) =>{
                return <Note 
                  key={index}
                  id={index}
                  title={val.title}
                  content={val.content}
                  deleteItem={onDelete}
                />
            })}        
          <Footer />
        </>
    )
}

export default Clone;
