import ReactDOM from 'react-dom';
import './index.css';

let time = new Date().toLocaleTimeString();
console.log(time);
let wish = "";

if(time < '11:00:00 AM'){
    wish = ', Good Morning';
    let obj = { color : 'orange'}


}else if(time < '7:00:00 PM'){
   wish = ', Good Afternoon';
   let obj = { color : 'green'}
}else{
  wish = ', Good Night';
  let obj = { color : 'black'}
}

ReactDOM.render( <React.Fragment>
  <h1 className="heading" style="{obj.color}">Hello sir {wish}</h1>
</React.Fragment> ,document.getElementById('root'))
