import React, { useState } from 'react';

const Form = () =>{

   let [fullName, setFullName] =  useState({
       fname : '',
       lname : ''

   });
   
    const inputEvent = (event) =>{
        // console.log('change');
        // console.log(event.target.value);;;
        console.log(event.target.name);
        console.log(event.target.placeholder);
        let value = event.target.value;
        let name = event.target.name;
        setFullName((preValue) =>{
             console.log(preValue);
             if(name === 'fName'){
                 return {
                       
                    fname : value,
                    lname : preValue.lname

                 }
              
             }else if(name === 'lName'){
                return {
                       
                    fname : preValue.fname,
                    lname : value

                 }
             }
        });
      
       
    }

  

    const onSubmit = (event)=>{
        alert('on Submitted')
        
        event.preventDefault();
    }
   return (
       <>
       <form onSubmit={onSubmit}>
       <div>
           <h1>Hello {fullName.fname} {fullName.lname}</h1>
           <input  type='text' placeholder="Enter Your Name" onChange={inputEvent} value={fullName.fname} name="fName" />
           <br />
           <input  type='text' placeholder="Enter Your Last Name" onChange={inputEvent} value={fullName.lname} name="lName" />
           
           <button type="submit" > Click Me üëç </button>
       </div>
       </form>
       </>
   )
}

export default Form;
