import Rect, { useState } from 'react';


const Digital = () =>{
    let time = new Date().toLocaleTimeString();
    let [Digitaly, UpdateTime] = useState(time);
    const Update= () =>{
        setInterval(() =>{
            time = new Date().toLocaleTimeString();
            UpdateTime(time);
        },1000)
    }

   
    return(
        <>
        <h1>{Digitaly}</h1>
        <button onClick={Update}></button>
        </>
    );
}

export default Digital;
