import React from 'react';
import ReactDOM from 'react-dom';

let Greting = '';
let curDate = new Date(2021,2,5,5);
curDate = curDate.getHours();
const css = {}
if(curDate <= 11 && curDate > 1){
   Greting = 'Good Mornig';
   css.color  = 'orange';
}else if(curDate > 12 && curDate < 19){
  Greting = 'Good Afternoon';
  css.color = 'green';
}else{
  Greting = 'Good Night';
  css.color = 'black';
}

ReactDOM.render(
<><h1>Hello sir , <span style={css}>{Greting}</span></h1></> ,
  document.getElementById('root'))
